import IConsoleManager from './IConsoleManager';

/**
 * The central management point for console-based logging packages.
 */
export class ConsoleManager implements IConsoleManager {
    public suppressMessages: boolean = false;

    public callWithMessageSuppression<T>(cb: () => T): T {
        if (cb) {
            const originalValue = this.suppressMessages;

            this.suppressMessages = true;

            try {
                return cb();
            }
            finally {
                this.suppressMessages = originalValue;
            }
        }
    }
}

export default ConsoleManager;
