/**
 * The central management point for console-based logging packages.
 */
export interface IConsoleManager {
    /**
     * Gets or sets whether Glimpse messages for writes to the console should be suppressed.
     */
    suppressMessages: boolean;
    /**
     * Wraps suppression of Glimpse messages (i.e. disables, then enables) for writes to the console.
     *
     * @param cb The function to call while messages are suppressed.
     */
    callWithMessageSuppression<T>(cb: () => T): T;
}
export default IConsoleManager;
